import Slider from 'react-animated-slider';
import 'react-animated-slider/build/horizontal.css';
{/* <iframe width="1293" height="480" src="https://www.youtube.com/embed/KV4wctXNwhA" title="Anna Dhanam | Food Donation | అన్న దానం | التبرع بالطعام | Free Food | Street Food Planet" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe> */}
import poster1 from '../assets/poster1.png';
import poster2 from '../assets/poster2.png';
import React from 'react';

function Card() {
    const slides = [
        { title: 'img', media: poster1},
        { title: 'img', media: poster2},
        { title: 'vid', media: `url(https://www.youtube.com/embed/KV4wctXNwhA)`}
      ];
  return (
    <div>
        <Slider>
        {slides.map((slide, index) => {
            if(slide.title==='img') <img src={slide.media} alt="poster"/>
            else <iframe width="1293" height="480" src={slide.media} title="Anna Dhanam | Food Donation | అన్న దానం | التبرع بالطعام | Free Food | Street Food Planet" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture" allowfullscreen></iframe> 

        })

        }
        </Slider>
    </div>
  );
}

export default Card
